from django import forms
from .models import EmailContact

class EmailContactForm(forms.ModelForm):

    nombre = forms.CharField(widget=forms.TextInput(attrs={"class":"bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500", "placeholder":"Nombre..."}))
    empresa = forms.CharField(widget=forms.TextInput(attrs={"class":"bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500", "placeholder":"Empresa..."}))
    correo = forms.CharField(widget=forms.TextInput(attrs={"class":"bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500", "placeholder":"correo@ejemplo.com" }))
    mensaje = forms.CharField(widget=forms.TextInput(attrs={"class":"block w-full pb-4 mb-5 text-gray-900 border border-gray-300 rounded-lg bg-gray-50 sm:text-md focus:ring-blue-500 focus:border-blue-500 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500","placeholder":"Mensaje..."}))

    class Meta:
        model = EmailContact
        fields = ['nombre','empresa','correo','mensaje']